import { useState } from "react";

export const FolderItem = ({fileItem}) => {
    const [open, setOpen] = useState(false);
    return (
        <div>
            <div onClick={() => setOpen((prev) => !prev)}>{fileItem.name} {open ? '<' : '>'}</div>
            {open && fileItem?.children?.length > 0 && fileItem.children.map((child) => <FolderItem fileItem={child} />)}
        </div>
    )
}
