import { useState } from "react";

export const FolderItem = ({fileItem, addData}) => {
    const [open, setOpen] = useState(false);

    const addFileFolder = () => {
        const name = prompt('Enter file name');
        const type = prompt('Enter file type');
        const newItem = {
            name: name,
            id: `${fileItem.id}/${name}`,
            type: type,
            ...(type === "folder" && {children: []})
        }
        addData(newItem, fileItem.id);
    }
    return (
        <div>
            <div onClick={() => setOpen((prev) => !prev)}>{fileItem.name} {fileItem?.children?.length > 0 && (open  ? '<' : '>')}
                {fileItem.type === "folder" && <button onClick={() => addFileFolder(fileItem.id)}>Add File or Folder</button> }
            </div>
            {open && fileItem?.children?.length > 0 && fileItem.children.map((child) => <ul key={child.id}><FolderItem fileItem={child} addData={addData}/></ul>)}
        </div>
    )
}
